INSERT INTO `usercomment` VALUES (1,'我觉得很对',4,12,'abc'),(2,'不行啊',6,12,'王五'),(3,'本周，非洲国家乍得突然宣布，总统代比在距首都只有300公里的前线，遭反政府武装袭击，不幸去世。军队还宣布宵禁，切断了国内所有路上关卡，说是为了“维护国家的统一与稳定”，但实际是把指挥棒交给了总统儿子，还是父死子继那一套。其实，无论是哪一方取得胜利，乍得这个“国家”都没有未来',5,12,'杨栩'),(4,'本周，非洲国家乍得突然宣布，总统代比在距首都只有300公里的前线，遭反政府武装袭击，不幸去世。军队还宣布宵禁，切断了国内所有路上关卡，说是为了“维护国家的统一与稳定”，但实际是把指挥棒交给了总统儿子，还是父死子继那一套。其实，无论是哪一方取得胜利，乍得这个“国家”都没有未来',5,12,'杨栩'),(5,'本周，非洲国家乍得突然宣布，总统代比在距首都只有300公里的前线，遭反政府武装袭击，不幸去世。军队还宣布宵禁，切断了国内所有路上关卡，说是为了“维护国家的统一与稳定”，但实际是把指挥棒交给了总统儿子，还是父死子继那一套。其实，无论是哪一方取得胜利，乍得这个“国家”都没有未来',5,12,'杨栩'),(6,'本周，非洲国家乍得突然宣布，总统代比在距首都只有300公里的前线，遭反政府武装袭击，不幸去世。军队还宣布宵禁，切断了国内所有路上关卡，说是为了“维护国家的统一与稳定”，但实际是把指挥棒交给了总统儿子，还是父死子继那一套。其实，无论是哪一方取得胜利，乍得这个“国家”都没有未来',5,12,'杨栩'),(7,'本周，非洲国家乍得突然宣布，总统代比在距首都只有300公里的前线，遭反政府武装袭击，不幸去世。军队还宣布宵禁，切断了国内所有路上关卡，说是为了“维护国家的统一与稳定”，但实际是把指挥棒交给了总统儿子，还是父死子继那一套。其实，无论是哪一方取得胜利，乍得这个“国家”都没有未来',5,12,'杨栩'),(8,'那么mrna是什么呢？',7,12,'yangzehao'),(9,'还行',5,12,'杨栩'),(10,'还是可以用的。\r\n',7,44,'yangzehao'),(11,'高考啊，真是怀念啊。',7,45,'yangzehao'),(12,'坐等吃瓜',7,46,'yangzehao'),(13,'散户接盘罢了。',7,47,'yangzehao'),(14,'是本好书。',7,48,'yangzehao'),(15,'我不是。',7,49,'yangzehao'),(16,'好好读书吧。。。',7,50,'yangzehao'),(17,'有点意思\r\n',7,51,'yangzehao'),(18,'还可以，慢慢开始度过人生吧。',7,52,'yangzehao'),(19,'《海底》',7,53,'yangzehao'),(20,'网易云',7,54,'yangzehao'),(21,'听不见的，不方便生存',7,55,'yangzehao'),(22,'还好，在穿鞋',7,69,'yangzehao'),(23,'人世无常，知足常乐',7,68,'yangzehao'),(24,'加油中国！！！',7,61,'yangzehao'),(25,'加油中国！！！',7,60,'yangzehao'),(26,'加油自己！！',7,59,'yangzehao');
